DROP TABLE IF EXISTS authors;
DROP TABLE IF EXISTS blogs;
DROP TABLE IF EXISTS locations;


CREATE TABLE authors(
                        author_id INT GENERATED ALWAYS AS IDENTITY,
                        author_name VARCHAR(255),
                        author_email VARCHAR(255),
                        PRIMARY KEY(author_id)
);
CREATE TABLE locations(
                          location_id INT GENERATED ALWAYS AS IDENTITY,
                          location_name VARCHAR(255),
                          location_city VARCHAR(255),
                          location_street VARCHAR(255),
                          location_zip INT,
                          PRIMARY KEY(location_id)
);
CREATE TABLE blogs(
                      blog_id INT GENERATED ALWAYS AS IDENTITY,
                      author_id INT,
                      location_id INT,
                      blog_title VARCHAR(255),
                      blog_text TEXT,
                      blog_isViewed BOOLEAN,
                      blog_numberOfViews BIGINT,
                      PRIMARY KEY(blog_id),
                      CONSTRAINT fk_author FOREIGN KEY(author_id) REFERENCES authors(author_id),
                      CONSTRAINT fk_location FOREIGN KEY(location_id) REFERENCES locations(location_id)
);


